import React, { useEffect, useState } from 'react'

const api_key = "z8i7LWv7RVXCr0DtibE6NSObFlAb3B6Y";
//const api_link = "https://api.nytimes.com/svc/search/v2/articlesearch.json?q=election&api-key="+api_key;

function NewsFeeds() {
    const [articles, setarticles] = useState([])
    const [term, setterm] = useState('everything')
    const [isLoading, setisLoading] = useState(true)

    useEffect(() => {
        const fetchArticles = async() => {
       try{

               const res = await fetch('https://api.nytimes.com/svc/search/v2/articlesearch.json?q='+term+'&api-key='+api_key)
               const articles = await res.json()
               //console.log(articles.response.docs)
               setarticles(articles.response.docs)
       }catch(error){
           console.error(error)
       }
    }
       fetchArticles()
    }, [])

    //console.log(api_link)
    return (
    <>
    <div>
    <form>
        <input
            type="text"
            id="header-search"
            placeholder="Search News posts"
            name="s" 
        />
        <button type="submit">Search</button>
    </form>
    </div>
    <div>
        <section>
            {articles.map((article) => {
                const {abstract,
                     headline: {main},
                      byline: {original},
                       lead_paragraph,
                        news_desk,
                         section_name,
                          web_url,
                           _id,
                            word_count} = article 
                return (
                    <article key={_id}>
                        <h2 className="font-bold text-2xl mb-2">{main}</h2>
                        <p>{abstract}</p>
                        <a href={web_url} target="_blank">Web Resource</a>
                        <p>{lead_paragraph}</p>

                        <ul className="my-4">
                            <li>{original}</li>
                            <spn className="font-bold">News Desk : </spn>{' '}<li>{news_desk}</li>
                            <span className="font-bold">Section Name : </span>{' '}<li>{section_name}</li>
                            <span className="font-bold">Word Count : </span>{' '}<li>{word_count}</li>
                        </ul>
                    </article>
                )
            })}
        </section>
    </div>
    </>
    )
}

export default NewsFeeds
